targets.expr:
sum by (id) (
  label_replace(
    (1 - avg by (instance) (irate(node_cpu_seconds_total{mode="idle"}[1m]))),
    "name", "$1", "instance", "(.*)"
  )
  * on(name) group_left(id)
    pve_guest_info{template!="1",name!~"ha|synology"}
)

sum by (id) (
  label_replace(
    (1 - (avg by (instance) (node_memory_MemAvailable_bytes) / avg by (instance) (node_memory_MemTotal_bytes))),
    "name", "$1", "instance", "(.*)"
  )
  * on(name) group_left(id)
    pve_guest_info{template!="1",name!~"ha|synology"}
)

sum by (id) (
  label_replace(
    (sum by (instance) (rate(node_network_receive_bytes_total{device!~"lo|docker.*|veth.*|br-.*"}[1m]))
      + sum by (instance) (rate(node_network_transmit_bytes_total{device!~"lo|docker.*|veth.*|br-.*"}[1m]))),
    "name", "$1", "instance", "(.*)"
  )
  * on(name) group_left(id)
    pve_guest_info{template!="1",name!~"ha|synology"}
)

sum by (id) (
  label_replace(
    (sum by (instance) (rate(node_disk_read_bytes_total[1m]))
      + sum by (instance) (rate(node_disk_written_bytes_total[1m]))),
    "name", "$1", "instance", "(.*)"
  )
  * on(name) group_left(id)
    pve_guest_info{template!="1",name!~"ha|synology"}
)
